INSERT INTO ?:product_reviews (product_review_id, product_id, user_id, name, advantages, disadvantages, comment, rating_value, ip_address, is_buyer, product_review_timestamp, country_code, city, reply_user_id, reply, reply_timestamp, helpfulness, status, storefront_id) VALUES
    (1, 228, 3, 'Иван Митрофанов', NULL, NULL, 'Я впервые увидел этот монитор в одном из компьютерных магазинов. Он действительно выделяется цветом и очень привлекает к себе внимание. Я смотрю на нем фильмы, играю в игры. Единственное, что я хотел бы изменить, это экран для обработки текстов (незначительная жалоба). Это очень хороший монитор и цена хорошая для 3D LCD.', 4, '7f000001', 'N', 1406114992, '', '', 0, NULL, 0, 0, 'A', 1),
    (2, 228, 0, 'Елена Круглова', '', 'Не получается настроить, чтобы изображение было красивым', 'Монитор слишком светлый, и регулировка контрастности и яркости не помогает. Независимо от того, что настройка яркости и контрастности используется, все так или иначе слишком тусклое, серое или слишком светлое. Яркость и контрастность монитора, кажется, имеют одинаковую функцию регулировки. При изменении контрастности, кажется, на самом деле изменяется яркость и наоборот. Я, вероятно, верну его в ближайшее время, если мне никто не сможет правильно настроить яркость / контрастность.', 2, '7f000001', 'N', 1406156189, '', '', 1, 'Сожалеем, что монитор вам не подошёл. Попробуйте в настройках драйвера видеокарты включить полный динамический диапазон. После этого картинка становится ярче.', 1618555304, 0, 'A', 1),
    (3, 242, 0, 'Татьяна Вершкова', NULL, NULL, 'Просто идеальный!!!', 5, '7f000001', 'N', 1406179040, '', '', 0, NULL, 0, 0, 'A', 1),
    (4, 242, 0, 'Олег Диментьев', NULL, NULL, 'Мне не понравился.', 2, '7f000001', 'N', 1406210565, '', '', 0, NULL, 0, 0, 'A', 1),
    (5, 78, 3, 'Екатерина Воротникова', NULL, NULL, 'Очень красивые сапоги. Они самые лучшие.', 4, '7f000001', 'N', 1406274497, '', '', 0, NULL, 0, 0, 'A', 1),
    (6, 224, 0, 'Александр Краснов', NULL, NULL, 'Я приобрел Galaxy Tab в пятницу. Экран имеет очень широкий угол обзора, прежде чем приобрести Galaxy Tab, я смотрел Ipad2, и не нашел различия, кроме памяти.\n\nЯ уверен, что Galaxy Tab лучше.', 4, '7f000001', 'N', 1406299810, '', '', 0, NULL, 0, 0, 'A', 1),
    (7, 224, 0, 'Антон Лисицин', 'Легкий, диагональ 8.9 самая оптимальная.', 'Есть моменты, которые раздражают при перевороте, ориентация экрана менялась с задержкой, при переключении между экранами тоже есть случаи зависания.', 'Брал с расчетом на компактность и быстродействие. Samsung Galaxy мой самый любимый.', 5, '7f000001', 'N', 1406343819, '', '', 0, NULL, 0, 0, 'A', 1),
    (8, 5, 0, 'Александр Дмитриев', NULL, NULL, 'Я очень доволен этим ЖК телевизором. Я искал и читал все о телевизорах с плоским экраном. Я так впечатлен качеством изображения и звука, даже при том, что в некоторых отзывах я читал что качество звука и объем не очень. В целом, преимуществами являются: ​встроенный динамик, яркий дисплей, реалистичные цвета, легкий. Определенно рекомендовал бы это своим друзьям.', 4, '7f000001', 'N', 1406400805, '', '', 0, NULL, 0, 0, 'A', 1),
    (9, 1, 0, 'Михаил Ворошилов', NULL, NULL, 'Наслаждаюсь этим телевизором. Цвета удивительны, глубина, баланс и все остальное просто на высшем уровне! Я могу подключить свой жесткий диск и смотреть видео мгновенно! Я до сих пор поражен ясностью. Есть возможность Blue-Ray. Хотя цена относительно высока, я могу сказать, что это удивительное произведение технологического мастерства.', 4, '7f000001', 'N', 1406428012, '', '', 0, NULL, 0, 0, 'A', 1),
    (10, 23, 0, 'Елена Бушуева', NULL, NULL, 'Приобрел домашний кинотеатр. Я немного опасался системы 5,1 Channel, но решили дать ему попытку. Звук хороший, хотя средний диапазон немного яснее. Система определенно не хватает низких частот, так что я в настоящее время ищу хороший сабвуфер. Высокие частоты кристально чистые, хотя объемное звучание. Я бы не рекомендовал его всем, но он определенно достаточно хорош в своем сегменте, хотя и не идеален.', 3, '7f000001', 'N', 1406507980, '', '', 0, NULL, 0, 0, 'A', 1),
    (11, 8, 0, 'Ольга Дмитриева', NULL, NULL, 'Отличное качество изображения ЖК-диспля на основе LED, с относительно глубокими уровнями для темных сцен, также хороший баланс белого. Она включает в себя встроенный Wi-Fi, 3D очки, и много других интересных функций. В целом, это довольно хороший телевизор, хотя картина в темных сценах иногда теряется под определенным углом.', 4, '7f000001', 'N', 1406526232, '', '', 0, NULL, 0, 0, 'A', 1),
    (12, 172, 0, 'Николай Волин', NULL, NULL, 'Я искал простую и удобную в использовании видеокамеру. Когда я увидел T10 Full HD, она мне показалась действительно отличной видеокамерой! Если вы просто хотите снять видео, или планируют сделать полный видео проект - эта видеокамера станет вашим лучшим другом! В ней есть все необходимые функции, которые легко регулируется. Я не сталкивался с проблемами в использовании этой камеры. Определенно стоит своих денег!', 4, '7f000001', 'N', 1406579316, '', '', 0, NULL, 0, 0, 'A', 1),
    (13, 238, 0, 'Светлана Иванова', NULL, NULL, 'Хороший и надежный телефон является необходимым для человека, как я, вот почему я решил выбрать iPhone. Apple, является замечательной компанией, и идет всегда в ногу со временем, дальновидный и инновационный. IPhone прост в использовании и, безусловно, самый стильный телефон на рынке в настоящее время. Наслаждаюсь этим, хотя иногда испытываю проблемы с программным обеспечением и обслуживанием. Есть телефон буквально напичканы большим количеством функций, которые не используют. Я бы рекомендовал это в основном молодому поколению, а людям старшего поколения это не столь необходимо.', 4, '7f000001', 'N', 1406628187, '', '', 0, NULL, 0, 0, 'A', 1),
    (14, 238, 0, 'Нина Цифаркина', NULL, NULL, 'Отличный телефон! Действительно удобен и прост в пользовании. У меня был выбор между Galaxy SIII, Lumia 920, HTC J Butterfly, ну и Iphone. Я взяла Iphone, потому что в нем нет ничего лишнего и приложения не зависают. Даже если очень захотеть, то тормозить все равно не будет. Так же у него прекрасная камера, довольно резвый браузер.\n\n\n\nНедостатки: Зарядка в принципе извечная проблема со всеми новыми телефонами, но мне её вполне хватает.', 3, '7f000001', 'N', 1406655145, '', '', 0, NULL, 0, 0, 'A', 1),
    (15, 167, 0, 'Елена Николаева', '', '', 'Я сначала долго думал что выбрать Nikon или Canon, но когда у меня появился шанс сравнить две модели 2 конкурирующих гигантов, я выбрал Nikon. Вы знаете, с ним чувствуешь себя хорошо. Цвета, матрица, интерфейс - превзошли все ожидания! Я уже сделал портфолио с его помощью. Просто прекрасный!', 5, '7f000001', 'N', 1406703330, '', '', 1, 'Рады слышать, что фотоаппарат вам понравился. Не покажете снимки, которые с его помощью удалось сделать? ;-)', 1618553520, 0, 'A', 1),
    (16, 167, 0, 'Виктория Краснова', NULL, NULL, 'Не фотоаппарат, а конфетка просто. Не пожалела денег и купила его, он того стоит. Снимки получаются живыми и реалистичными. Со временем собираюсь приобрести новый объектив. В отличии от Canon, это просто лучшая фотокамера. Думала насчет Canon 550D, но потом поменяла свое решение, когда узнала о данной модели. Получаются отличные снимки в действии, портретные снимки. Быстро фокусируется! Единственно снимки на дальние расстояния меня не вдохновили, скорее всего это минус объектива. Но это просто мелочь, на них не стоит концентрировать свое внимание.\n\nВсем советую приобрести этот фотоаппарат!', 5, '7f000001', 'N', 1406760950, '', '', 0, NULL, 0, 0, 'A', 1),
    (17, 239, 0, 'Олег Фуражкин', NULL, NULL, 'Я пользовался iPhone 5 и iPhone 5s каждый день в течение прошлого года, кажется, они похожи: те же загнутые края, габариты. Только изменилась кнопка Home, камера, вспышка, которая заставить вас осознать, что это не iPhone 5. Многие люди думают, что выпустив тот же дизайн, два раза-это плохо, и есть другие, которые понимают, что иногда нет необходимости перемен. Apple, возможно понимает, что конкуренция сильна, и она нуждается, чтобы оставаться актуальными.', 3, '7f000001', 'N', 1406776470, '', '', 0, NULL, 0, 0, 'A', 1),
    (18, 239, 0, 'Ольга Сердцева', NULL, NULL, 'Аппарат отличный, не зависает, все удобно и просто.Те кто говорят что у него куча проблем и он такой сякой просто либо противники Apple, но лично у меня все без глюков и проблем.\n\nМоя оценка твердая 5.', 5, '7f000001', 'N', 1406853359, '', '', 0, NULL, 0, 0, 'A', 1),
    (19, 29, 0, 'Илья Ефимов', NULL, NULL, 'Довольно прост в использовании. Обнаружение маршрута занимает много времени! Также у меня были проблемы с получением обновления карт.\n\nВ целом, это хороший GPS-навигатор и я с ним еще ни разу не заблудился.', 4, '7f000001', 'N', 1407149566, '', '', 0, NULL, 0, 0, 'A', 1),
    (20, 29, 0, 'Иван Митрофанов', NULL, NULL, 'Фантастическая штука, очень быстрая.\n\nСтоит купить. Мне нравится, как он меняет маршрут. Цена для него маленькая.', 4, '7f000001', 'N', 1407181524, '', '', 0, NULL, 0, 0, 'A', 1),
    (21, 30, 0, 'Ольга Сердцева', NULL, NULL, 'Интерфейс на основе браузера является Glitchy. Иногда он подключается сразу. Иногда мне приходится подключать и отключать его несколько раз. Явно не готов к продаже.', 3, '7f000001', 'N', 1407210830, '', '', 0, NULL, 0, 0, 'A', 1),
    (22, 30, 0, 'Виктория Краснова', NULL, NULL, 'Намного лучше, чем Garmin-х. Объекты не появляются на карте.\n\nЕсли вы едите сверх лимита отображает скорость в красном цвете.\n\nОпределяет маршруты с задержкой.', 2, '7f000001', 'N', 1407278680, '', '', 0, NULL, 0, 0, 'A', 1),
    (23, 32, 0, 'Олег Фуражкин', NULL, NULL, 'Я очень рад что купил этот навигатор. Я использую его в поездках и не имели никаких проблем с ним. Программное обеспечение, работает отлично. Я даже скачал несколько пользовательских POI, и они работали здорово. Автоматическое масштабирование и уменьшение не так плохо, как некоторые говорят. Номера телефонов и вся информация о каждом POI есть, вы просто должны нажать на информационную вкладку, чтобы его увидеть.', 5, '7f000001', 'N', 1407328478, '', '', 0, NULL, 0, 0, 'A', 1),
    (24, 32, 0, 'Максим Иванов', NULL, NULL, '3450 имеет хорошую цену, более быстрый процессор, сенсорный экран, отличную графику.\n\nЭто качественный GPS-навигатор.\n\nСПАСИБО!', 4, '7f000001', 'N', 1407341177, '', '', 0, NULL, 0, 0, 'A', 1),
    (25, 33, 0, 'Ольга Сердцева', '', '- Время задержки связи со спутниками. Иногда он не может найти их вообще.\r\n- Часто не правильно показывает, где я нахожусь.\r\n- При поиске самых близких мест, он показывает мне самые отдаленные.\r\n- Неправильно рассчитывает расстояние.\r\n- Крепление не держится на лобовом стекле.', 'После обновления карт ничего не изменилось. Навигатор не понравился, скорее всего его верну.', 1, '7f000001', 'N', 1407396365, '', '', 1, 'Сожалеем, что вы столкнулись с проблемой. Возможно, это заводской брак? Мы можем предложить вам другую модель или заменить на другой такой же. Просто напишите нам!', 1618552719, 0, 'A', 1),
    (26, 33, 0, 'Леонид Ефимов', NULL, NULL, 'Я не могу поверить, что так долго обходился без GPS!! Я использую его, чтобы  определить маршрут. Этот GPS прекрасно работает, и это так удобно. Единственный недостаток его трудно выключить, я должен несколько раз коснуться экрана, прежде чем он выключится.', 4, '7f000001', 'N', 1407448500, '', '', 0, NULL, 0, 0, 'A', 1),
    (27, 37, 0, 'Елена Николаева', NULL, NULL, 'Очень довольна приобретением. Стерео достаточно громкое. Единственный недостаток, устройство не имеет BlueTooth.', 4, '7f000001', 'N', 1407493299, '', '', 0, NULL, 0, 0, 'A', 1),
    (28, 37, 0, 'Иван Колышев', NULL, NULL, 'Я установил это устройство в мою машину Ford. Для установки я просто подключил соответствующие провода моей машины.\n\nПосле установки устройство загрузило все мои песни с USB-накопителя, также проигрывает песни с диска. Звук просто отличный. Мне также нравится, что я могу сделать дисплей более темным.\n\nТакже у меня есть жалоба. Как установить часы и цвета дисплея я очень долго искал в интернете, потому что я не мог найти данные настройки в инструкции.', 3, '7f000001', 'N', 1407524878, '', '', 0, NULL, 0, 0, 'A', 1),
    (29, 39, 0, 'Николай Ворошилов', NULL, NULL, 'Это простая в управлении автомагнитола. Огромный плюс возможность обновления программы. Bluetooth звонки и интеграция с iPhone безупречна. Потоковое BT аудио звучит не так хорошо, что делает качество заметно ниже.\n\nВключенные функции DSP дают огромный выбор звуковых вариантов. Любимые особенности: слот SD позади лицевой панели. Регулировка уровня позволяет поддерживать несколько уровней громкости через различные источники.\n\nОчень рекомендую приобрести.', 5, '7f000001', 'N', 1407569835, '', '', 0, NULL, 0, 0, 'A', 1),
    (30, 39, 0, 'Юрий Кондрашов', NULL, NULL, 'Просто супер. Сбалансированный ровный звук. Является редким устройством, который имеет все необходимое, идеально подходит для меня. Я так рад, что приобрел его.', 5, '7f000001', 'N', 1407630893, '', '', 0, NULL, 0, 0, 'A', 1),
    (31, 40, 0, 'Александр Дмитриев', NULL, NULL, 'Я заказал эту  автомагнитолу для моего маленького грузовика. Устройство установилась быстро. Функция HD-радио является фантастическим. Bluetooth работает очень хорошо, и есть много дополнительных кабелей. Легкое подключение к Android и качество звука хорошее. Настройка не такая уж сложная, просто не торопитесь и следуйте инструкциям. Есть пульт дистанционного управления.\n\nЯ рекомендую это устройство друзьям!', 5, '7f000001', 'N', 1407653480, '', '', 0, NULL, 0, 0, 'A', 1),
    (32, 40, 0, 'Раиса Малышева', NULL, NULL, 'Я была настроена скептически к этому устройству, но после его приобретения была очень рада. Это очень мощное устройство. Легко настраивается и имеет много цветов на выбор для освещения. Недостатком является то, что я не могу добавлять радиостанции.', 4, '7f000001', 'N', 1407693475, '', '', 0, NULL, 0, 0, 'A', 1),
    (33, 42, 0, 'Нина Гаврилова', NULL, NULL, 'Я купила эту аудиосистему как подарок другу, потому что это то, что он попросил! Это стерео именно то, что он хотел, и он цена меня полностью устраивала! Он проигрывает музыку с компакт дисков! Так же есть тюнер. Товар полностью соответствует описанию в магазине!', 5, '7f000001', 'N', 1407751490, '', '', 0, NULL, 0, 0, 'A', 1),
    (34, 42, 0, 'Антон Михайлов', NULL, NULL, 'Я купил Pioneer DEH-1300MP, потому что другой мой стерео сломался. Мне понравилась данная модель в связи с относительно низкой ценой, маркой Pioneer, достаточной мощностью, а также тем фактом, что у него есть все возможности, которые меня интересуют.\n\nПосле установки я включил его и не мог поверить, насколько хорошо он звучит по сравнению с моим старым. Я могу проигрывать на этом MP3-плеер все компакт-диски.', 5, '7f000001', 'N', 1407797507, '', '', 0, NULL, 0, 0, 'A', 1),
    (35, 43, 0, 'Татьяна Вершкова', NULL, NULL, 'Эта автомагнитола, то что я хотела. Есть Bluetooth подключение довольно не навязчивое. Эквалайзер легко изменяется, меню довольно легкое. Дисплей сенсорный яркий с голубым освещением.', 5, '7f000001', 'N', 1407840070, '', '', 0, NULL, 0, 0, 'A', 1),
    (36, 43, 0, 'Иван Митрофанов', NULL, NULL, 'Автомагнитола просто супер. Я могу добавлять радиостанции. Качество Bluetooth на высшем уровне, соединяется очень легко. Единственное что мне не понравилось это стерео.', 3, '7f000001', 'N', 1407872619, '', '', 0, NULL, 0, 0, 'A', 1),
    (37, 48, 0, 'Петр Горбунов', NULL, NULL, 'Эти динамики отлично работают, я установил их под задним сиденьем и они звучат потрясающе. Я искали динамики, которые бы играли громко. Эти колонки не разочаровали. Я могу слушать весь день музыку без зарядки аккумулятора.', 5, '7f000001', 'N', 1407896777, '', '', 0, NULL, 0, 0, 'A', 1),
    (38, 48, 0, 'Иван Колышев', NULL, NULL, 'Я был очень впечатлен исполнением этих динамиков. 8-дюймовые конусы действительно предоставляют много баса. Первоначально я ожидал, что эти динамики могут преждевременно выйти из строя, но они работают до сих пор!\n\nНедостаток: Эти негабаритные громкоговорители требуют большой мощности, для достижения максимальной производительности.', 3, '7f000001', 'N', 1407941592, '', '', 0, NULL, 0, 0, 'A', 1),
    (39, 49, 0, 'Александр Краснов', NULL, NULL, 'Купил эти колонки и ни разу не разочаровался в них. Они имеют отличное звучание. Они рассчитаны на питание от усилителя Clarion. Качество динамиков высшего уровня. Если вы ищете качественный динамик с качественным звуком, который не будет очень дорого, то это то что вам нужно.', 5, '7f000001', 'N', 1408019320, '', '', 0, NULL, 0, 0, 'A', 1),
    (40, 49, 0, 'Олег Фуражкин', NULL, NULL, 'Я купил два комплекта для себя и для друга. У меня не было никаких проблем с установкой, и они оказались не большими. Единственное, качество звука хотелось чтобы было лучше.', 4, '7f000001', 'N', 1408023159, '', '', 0, NULL, 0, 0, 'A', 1),
    (41, 50, 0, 'Нина Цифаркина', NULL, NULL, 'Я купила из взамен моих старых колонок. Они звучат гораздо лучше. Я положила перегородки позади колонок, чтобы защитить их, и улучшить звук. Единственное, что мне не нравиться, то что Pioneer изменил металлический материал на пластиковый, поэтому оценка 3.', 3, '7f000001', 'N', 1408069150, '', '', 0, NULL, 0, 0, 'A', 1),
    (42, 50, 0, 'Ольга Сердцева', NULL, NULL, 'Звук отличный. Высокочастотные динамики, довольно хороши. Они в основном являются мини сабвуферами.\n\nВ целом я счастлива и безусловно не раскаиваюсь в покупке.', 4, '7f000001', 'N', 1408121201, '', '', 0, NULL, 0, 0, 'A', 1),
    (43, 51, 0, 'Александр Дмитриев', NULL, NULL, 'Динамики огромные, качество отличное. Громкоговорители звучат великолепно. Они поражают низкими нотами!\n\nБольшое спасибо!', 4, '7f000001', 'N', 1408179078, '', '', 0, NULL, 0, 0, 'A', 1),
    (44, 51, 0, 'Алексей Моисеев', NULL, NULL, 'Эти колонки смешные, но очень хорошего качества. Они имеют невероятные, минимальные уровни сабвуфера. Я буквально пугаюсь, когда их слушаю!\n\nОпределенно рекомендовал бы их.', 4, '7f000001', 'N', 1408213947, '', '', 0, NULL, 0, 0, 'A', 1),
    (45, 52, 0, 'Светлана Виноградова', NULL, NULL, 'Я полюбила этот сабвуфер, как только вынула его из коробки. Он очень громкий. Kenwood производит качественный товар. Хорошая мощность с низким уровнем искажений. Если вы ищете удивительную систему бюджетного варианта, я рекомендую их к вам.\n\nНадеюсь этот отзыв кому-нибудь поможет.', 5, '7f000001', 'N', 1408267258, '', '', 0, NULL, 0, 0, 'A', 1),
    (46, 52, 0, 'Леонид Ефимов', NULL, NULL, 'Очень доволен этим сабвуфером, работает отлично, лучшая цена. Я уже думаю приобрести еще один.\n\nЯ рекомендую всем, кому нужно отличное звучание! ', 5, '7f000001', 'N', 1408286380, '', '', 0, NULL, 0, 0, 'A', 1),
    (47, 53, 0, 'Илья Ефимов', NULL, NULL, 'Я приобрел этот сабвуфер давно. Он не гремит, бас чистый, свежий, без искажений. Я использую 4 канала 1600W усилителя с АПЛ. Также у него высокая мощность генератора.\n\nПросто отличный сабвуфер!', 4, '7f000001', 'N', 1408328796, '', '', 0, NULL, 0, 0, 'A', 1),
    (48, 53, 0, 'Виктор Вединов', NULL, NULL, 'У меня было много сабвуферов от Kicker до Alpine. Купив этот сабвуфер я был поражен насколько хорошо он передает каждую ноту. Я не был удивлен, узнав, что этот сабвуфер был оценен, как лучший, по качеству и звуку.\n\nПоверьте мне ... Вы не будете разочарованы.', 5, '7f000001', 'N', 1408405832, '', '', 0, NULL, 0, 0, 'A', 1),
    (49, 54, 0, 'Иван Колышев', NULL, NULL, 'Я купил сабвуфер, чтобы установить в машину. Он просто замечателен!!! Я установил его в герметичный корпус, запускал на 1 Ом и он отлично работает. Сабвуфер отлично звучит при низких частотах. Он являются чемпионом.', 4, '7f000001', 'N', 1408424949, '', '', 0, NULL, 0, 0, 'A', 1),
    (50, 54, 0, 'Виктория Краснова', NULL, NULL, 'Это лучший сабвуфер. Он может производить звуки и вибрации как ни один другой сабвуфер. У него великолепное звучание. Даже несмотря на то, что его частотная характеристика не совсем сбалансирована, звучит он все равно здорово. Очень чистый низкий бас.\n\nСпасибо за замечательный продукт!', 5, '7f000001', 'N', 1408495183, '', '', 0, NULL, 0, 0, 'A', 1),
    (51, 55, 0, 'Раиса Малышева', NULL, NULL, 'Я купила этот усилитель за его небольшие габариты и низкие тепловыделения. Она прекрасно вписывается, так что становится полностью невидимой, но по-прежнему доступны в случае необходимости.\n\nЦена понравилась, доставка быстрая.', 4, '7f000001', 'N', 1408499523, '', '', 0, NULL, 0, 0, 'A', 1),
    (52, 55, 0, 'Виктория Краснова', NULL, NULL, 'Этот усилитель имеет небольшой вес с чистым звуком. Это мой первый сабвуферный усилитель! На большой мощности звук не такой качественный, как на среднем и низком уровне, но для обычного любителя вполне ничего!', 4, '7f000001', 'N', 1408581264, '', '', 0, NULL, 0, 0, 'A', 1),
    (53, 56, 0, 'Виктор Вединов', NULL, NULL, 'Действительно качественный звук! Очень доволен, идеально вписался в машину. Устраивает все. Конечно сложновато управление, но есть инструкция. Быстро установил. Для своей цены – просто шик, а не усилитель!', 5, '7f000001', 'N', 1408584075, '', '', 0, NULL, 0, 0, 'A', 1),
    (54, 56, 0, 'Нина Гаврилова', NULL, NULL, 'Я сравнивала несколько усилителей Pioneer и обнаружила, что это устройство более универсальное. В инструкции все подробно описано. Поиск места для установки было самым трудным.', 4, '7f000001', 'N', 1408654456, '', '', 0, NULL, 0, 0, 'A', 1),
    (55, 57, 0, 'Леонид Ефимов', NULL, NULL, 'Я купил этот усилитель взамен 1200 Вт Kenwood усилителю. Он очень качественный, имеет дистанционный плагин усиления. Я могу изменять коэффициент усиления для разных песен. Этот усилитель творит чудеса. Устанавливается очень легко и имеет синий светодиод.', 5, '7f000001', 'N', 1408700711, '', '', 0, NULL, 0, 0, 'A', 1),
    (56, 57, 0, 'Ольга Сердцева', NULL, NULL, 'Весит усилитель около 300 фунтов. Он прекрасно вписывается и здорово работает! Есть регулировка усиления. Никаких проблем с усилителем у меня пока не возникало.\n\nЯ рекомендую приобрести этот усилитель.', 5, '7f000001', 'N', 1408744292, '', '', 0, NULL, 0, 0, 'A', 1),
    (57, 58, 0, 'Николай Волин', NULL, NULL, 'Это устройство удивительное: яркий экран, возможность изменения изображения. Bluetooth сопряжение выполняется быстро и легко. Я могу делать небольшие изменения эквалайзера во время вождения. HD радио просто ошеломляет. Есть цифровое телевидение, радиостанции HD!', 5, '7f000001', 'N', 1408764885, '', '', 0, NULL, 0, 0, 'A', 1),
    (58, 58, 0, 'Светлана Иванова', NULL, NULL, 'Люблю это устройство. Включает в себя все хорошие функции. Аудио звук и видео отлично воспроизводятся. Есть возможность управления через телефон. Звук отличный! Большое количество опций!', 5, '7f000001', 'N', 1408822252, '', '', 0, NULL, 0, 0, 'A', 1),
    (59, 59, 0, 'Олег Фуражкин', NULL, NULL, 'У меня нет никаких проблем с ним. Очень рад, что купил.\n\nЯ использовал Bluetooth для подключения к моему телефону. Также использую USB-накопитель, чтобы проигрывать музыку, которую я скачал. Для переключения с USB-накопителя к радио и обратно требуется около 30 секунд. HD радио звучит здорово на любом канале.\n\nТаким образом, я сделал очень хорошее приобретение и получил массу удовольствий от его использования.', 5, '7f000001', 'N', 1408877007, '', '', 0, NULL, 0, 0, 'A', 1),
    (60, 59, 0, 'Елена Бушуева', NULL, NULL, 'Интерфейс очень хороший, быстрый, простой в использовании во время вождения. Переход от одной песни к другой происходит очень быстро.\n\nЗвук отличный. Только проблема с настройкой видео во время вождения.', 4, '7f000001', 'N', 1408920567, '', '', 0, NULL, 0, 0, 'A', 1),
    (61, 60, 0, 'Виктор Вединов', NULL, NULL, 'Прежде чем купить, я прочел множество отзывов. Мое первое впечатление, что он работает хорошо.\n\nЯ получил дополнительный кабель и он подключается к моему телефону. Радио прекрасно работает. Это моя любимая функция.\n\nСистема GPS работает быстро.\n\nЯ установил камеры заднего вида к данному устройству и она работает очень хорошо. Я был впечатлен качеством изображения.\n\nВсем советую приобрести.', 5, '7f000001', 'N', 1408966031, '', '', 0, NULL, 0, 0, 'A', 1),
    (62, 60, 0, 'Нина Гаврилова', NULL, NULL, 'Этот навигатор определенно стоит того, чтобы его купить. Он выглядит великолепно. Даже кнопки соответствуют интерьеру моей машины. Существует очень мало бликов на солнце. Есть процесс интеграции с Ipod, легко изменять список воспроизведения. Он также отображает обложки альбомов, которые я люблю. Он также имеет тюнер и встроенный HD радио тюнер.', 3, '7f000001', 'N', 1408991819, '', '', 0, NULL, 0, 0, 'A', 1),
    (63, 62, 0, 'Александр Краснов', NULL, NULL, 'Высший радар. Пользуюсь почти год. Никаких претензий. Ложных срабатываний почти нет.', 4, '7f000001', 'N', 1409037863, '', '', 0, NULL, 0, 0, 'A', 1),
    (64, 64, 0, 'Олег Диментьев', NULL, NULL, 'Я очень рад что приобрел Cobra, он спас меня дважды от получения штрафа. Ложные сигналы были, но очень редко. Мне нравиться функция компас и IntelliMute. Размер немного больше, что у других радаров-детекторов. Спиральный шнур идеально подходит для моего автомобиля.\n\nВ целом я очень доволен Cobra.', 4, '7f000001', 'N', 1409070939, '', '', 0, NULL, 0, 0, 'A', 1),
    (65, 64, 0, 'Екатерина Воротникова', NULL, NULL, 'Я купила этот детектор и очень довольна. Я живу в сельской местности и радар мне просто необходим при поездках в город. Следует отметить работает он очень хорошо. Несколько ложных тревог и случайных помех, но режим город прекрасно работает для ограничения ложных тревог. Это мой первый радар-детектор.', 4, '7f000001', 'N', 1409121360, '', '', 0, NULL, 0, 0, 'A', 1),
    (66, 65, 0, 'Александр Дмитриев', NULL, NULL, 'Cobra серии работает очень хорошо. При этом было 1-2 ложных тревоги за последние 2 месяца использования, в связи с нахождением рядом с автоматическими дверями на предприятие. Тем не менее я очень впечатлен этим радаром-детектором. Кроме того, у радара-детектора есть кнопка отключения звука. Металлический кронштейн очень прочно держит радар-детектор.', 4, '7f000001', 'N', 1409178008, '', '', 0, NULL, 0, 0, 'A', 1),
    (67, 65, 0, 'Елена Бушуева', NULL, NULL, 'Данная модель более чувствительная и заранее оповещает водителя. Он также обладает функциями обнаружения для области или местности. Хорошо продумана инструкция и пользовательские кнопки. Я испытала это устройство во многих городах. Независимо от того, где бы я не находилась он всегда меня предупреждал о наличие камеры на дороге.', 4, '7f000001', 'N', 1409214328, '', '', 0, NULL, 0, 0, 'A', 1),
    (68, 66, 0, 'Антон Лисицин', NULL, NULL, 'Это самый сложный высокочувствительный радар. Радар-детектор имеет много функций, чтобы снизить количество ложных предупреждений. Мне нравится функция IntelliMute. Экран видим при ярком дневном свете, есть функция затемнения. Цвета значков можно изменять. Сенсорный экран является бесценным.', 5, '7f000001', 'N', 1409264258, '', '', 0, NULL, 0, 0, 'A', 1),
    (69, 66, 0, 'Александр Краснов', NULL, NULL, 'Плюсами данного устройства являются, то что он цифровой радар с сенсорным экраном с легко читаемым дисплеем. Имеет степень защиты 360. Также плюсом является голосовые оповещения. Он содержит так много возможностей. Дополнительными особенностями я считаю, являются GPS Locator, предупреждение о низком заряде батареи и индикатор скорости.', 5, '7f000001', 'N', 1409315924, '', '', 0, NULL, 0, 0, 'A', 1),
    (70, 126, 0, 'Николай Волин', NULL, NULL, 'Работа магазина на высшем уровне, отправили в первый же будний день после заказа. Товаром доволен. Полная комплектация.\nРекомендую.', 5, '7f000001', 'N', 1409330869, '', '', 0, NULL, 0, 0, 'A', 1),
    (71, 205, 0, 'Светлана Виноградова', NULL, NULL, 'Эта вещь просто не работает. Заставила меня обновить прошивку, которая является бета-версией.\n\nИзбегайте D-Link в целом и данного маршрутизатора в частности!', 2, '7f000001', 'N', 1409372652, '', '', 0, NULL, 0, 0, 'A', 1),
    (72, 205, 0, 'Леонид Ефимов', NULL, NULL, 'Я долгое время использовал продукцию D-Link. Я купил этот маршрутизатор и с 1 дня у меня появились проблемы, он постоянно зависает и требует перезагрузки 2-3 раза в день.', 3, '7f000001', 'N', 1409418174, '', '', 0, NULL, 0, 0, 'A', 1),
    (73, 206, 0, 'Светлана Виноградова', NULL, NULL, 'Устройство вызывает больше положительных моментов. Из минусов - неадекватно высокая цена. Ну, греется хорошо, хотя не так и критично. Просто единственное, не стоит его прятать в очень маленькое пространство, например - ящик стола.\n\nИз плюсов: дизайн! Стильно впишется дома. Индикация не яркая, т.е. ночью не будет светильником. Но и в темноте не разберешь что за лампочка светится, все они близнецы. Легко настроил, хотя, если нужен только Wi-Fi то это явно расточительство, но если хотите получить большего, то откроете многое для себя! Функционал богатейший, а кому не хватает - ставьте альтернативные прошивки.\n\nP.s. оценку снизил только из-за очень большой цены!!!', 4, '7f000001', 'N', 1409483571, '', '', 0, NULL, 0, 0, 'A', 1),
    (74, 206, 0, 'Илья Ефимов', NULL, NULL, 'Наверное продукцией Asus, пользоваться уже не буду. Была модель этого производителя раньше, проработала 2 года и такая же история, как с новым. Только не выходит в интернет, а Wi-Fi есть. Фирма солидная, но наверное не в этом сегменте оборудования или как попадешь.', 2, '7f000001', 'N', 1409508452, '', '', 0, NULL, 0, 0, 'A', 1),
    (75, 207, 0, 'Раиса Малышева', NULL, NULL, 'Очень дорогое устройство, с кривыми драйверами если пытаться работать на 5 Гц больше 3-4 часов, скорость падает, иногда теряет соединение, притом с родным роутером от ASUS N66U. Лучше посмотреть в сторону других более дешевых аналогов.', 2, '7f000001', 'N', 1409543886, '', '', 0, NULL, 0, 0, 'A', 1),
    (76, 207, 0, 'Виктор Вединов', NULL, NULL, 'Решил попробовать ASUS USB-N53. Для меня выбор оказался удачным на все 100%! Никаких проблем с сетью не испытываю. Интернет устраивает полностью. Смотрю сериалы на турбо фильме в максимальном качестве, не выключая BitTorrent - кэширует моментально. В игры не играю, поэтому для моих задач - хватает с достатком. Самое главное, что не надо читать кучу FAQ на форумах, как пофиксить тот, или иной баг. Купил, воткнул, работает!', 4, '7f000001', 'N', 1409601536, '', '', 0, NULL, 0, 0, 'A', 1),
    (77, 208, 0, 'Иван Колышев', NULL, NULL, 'Я пробовал много камер безопасности на протяжении многих лет, и это является лучшей и одной из самых дешевых. Когда я впервые установил его, я был впечатлен качеством видео. Данная камера позволяет делать более резкие изображения.', 5, '7f000001', 'N', 1409622953, '', '', 0, NULL, 0, 0, 'A', 1),
    (78, 208, 0, 'Елена Николаева', NULL, NULL, 'Камера позволяет делать только 15 FPS при любом разрешении. Мало того, она имеет низкую скорость захвата. Если человек не стоит на месте, все что вы увидите, будет как в тумане.', 2, '7f000001', 'N', 1409704008, '', '', 0, NULL, 0, 0, 'A', 1),
    (79, 209, 0, 'Иван Колышев', NULL, NULL, 'Не советую. Пока настроишь аппарат, пока им воспользуешься - времени пройдет кучу. Раньше мечтал все старые снимки отсканировать. Данное устройство было куплено в основном для этих целей. Но потратив пол часа на 7 фотографий и оценив сколько недель работы предстоит впереди - бросил данное занятие. Сейчас использую только для копирования документов.', 3, '7f000001', 'N', 1409737479, '', '', 0, NULL, 0, 0, 'A', 1),
    (80, 209, 0, 'Светлана Виноградова', NULL, NULL, 'Удачная покупка! При такой низкой цене самого аппарата качество сканируемых фотографий и пленок превосходное. Для оцифровки домашнего архива (как бюджетный вариант) подходит идеально.\n\nКонечно, для негативов хотелось бы получить лучший результат. Но тут очень много зависит от самой пленки, ее состояния.\n\nС ПО у меня проблем не возникло, все работает, никаких сложностей нет. А то, что иногда медленно (негативы на большом разрешении), так это не критично.', 5, '7f000001', 'N', 1409775196, '', '', 0, NULL, 0, 0, 'A', 1),
    (81, 210, 0, 'Елена Круглова', NULL, NULL, 'Этот принтер недорогой, и прекрасно работает! Печатает красивые фотографии. Я очень счастлива что приобрела его. \"Все-в-одном\" принтер, сканер и копировальный аппарат. Я использую его постоянно! Моя единственная жалоба, что при использовании других не чернил HP, он не показывает уровень чернил.\n\nНо все же отличный продукт!', 4, '7f000001', 'N', 1409797519, '', '', 0, NULL, 0, 0, 'A', 1),
    (82, 210, 0, 'Иван Митрофанов', NULL, NULL, 'НР Photosmart Plus B210a отличный небольшой принтер все-в-одном. Он имеет множество функции, которые содержатся в более дорогих принтерах: сенсорный контроля, двойные лотки для бумаги и беспроводной связи.', 4, '7f000001', 'N', 1409845984, '', '', 0, NULL, 0, 0, 'A', 1),
    (83, 211, 0, 'Олег Фуражкин', NULL, NULL, 'Печатаю регулярно уже полгода, пока сменил один комплект картриджей и уже почувствовал разницу, так как тут за те же 3000 руб я купил комплект из четырех цветов. Функционал тоже выигрывает у лазерного принтера. Просто принтер,который стоит в дальнем углу и не требует никакого к себе внимания. При всём при том выдаёт прекрасные распечатки в цветном и ч/б исполнении. Из режима сна выходит за несколько секунд.\n\nКартриджи подходят различных производителей,поэтому стоимость печати удалось снизить до весьма приемлемой.', 5, '7f000001', 'N', 1409898082, '', '', 0, NULL, 0, 0, 'A', 1),
    (84, 211, 0, 'Татьяна Вершкова', NULL, NULL, 'Классный дизайн для машины - зверя. Быстро и легко осуществляется замена чернил, очень удобный лоток для бумаги. Возможности сканера и ксерокса поражают. Все делает быстро и качественно, нареканий по качеству выходящей продукции никаких. По сравнению со многими конкурентами интерфейсы заметно быстрее работают, да и нет перегрева при постоянной загрузке. Стоящая покупка, если нужен действительно помощник \"все в одном\"! Стоит своих денег и достаточно быстро окупается.', 5, '7f000001', 'N', 1409964803, '', '', 0, NULL, 0, 0, 'A', 1),
    (85, 212, 0, 'Иван Кудашов', NULL, NULL, 'Сканером пользуюсь постоянно и печатаю достаточно много, около 7000-8000 листов в месяц. По моему это внушительная цифра, в инструкции написано до 10000. С объемами он легко справляется. Очень привлекательная в первую очередь цена, не ожидал что он так долго будет работать под нагрузкой. Был приятно удивлен.', 5, '7f000001', 'N', 1410005921, '', '', 0, NULL, 0, 0, 'A', 1),
    (86, 212, 0, 'Илья Ефимов', NULL, NULL, 'Купил это мфу по совету знакомого. Разобрался быстро, да и ПО в комплекте идущее - тоже более чем легко в освоении оказалось. Печатает очень быстро, экономит кучу времени.\n\nРаботает он тихо, ничего нигде не шумит, корпус подогнан плотно.', 4, '7f000001', 'N', 1410014032, '', '', 0, NULL, 0, 0, 'A', 1),
    (87, 213, 0, 'Светлана Виноградова', NULL, NULL, 'Простота установки. Работает на отметке 29-35 градусов, разогнанных до 3,8 ГГц. Действительно хорошие инвестиции. Много встроенной графики.\n\nМинусов нет.', 5, '7f000001', 'N', 1410078344, '', '', 0, NULL, 0, 0, 'A', 1),
    (88, 213, 0, 'Нина Гаврилова', NULL, NULL, 'Прочный и надежный на воздушном охлаждении. Система с i7-3770K Ivy Bridge 3,5 ГГц.\n\nПрактически нет минусов, за исключением, цены.', 5, '7f000001', 'N', 1410131206, '', '', 0, NULL, 0, 0, 'A', 1),
    (89, 214, 0, 'Раиса Малышева', NULL, NULL, 'Прекрасный компьютер. Покупался для домашнего пользования. Подключен к телевизору 42\". Компактный корпус, влезет куда угодно.', 4, '7f000001', 'N', 1410138806, '', '', 0, NULL, 0, 0, 'A', 1),
    (90, 214, 0, 'Виктор Вединов', NULL, NULL, 'Небольшой (вполне уместится на письменном столе), высокая пропускная способность видеокарты в текущем ценовом диапазоне, система восстановления ОС (активируется нажатием F9).\n\nНедостаток: При первом запуске монитор не показывает картинку, нужно подождать несколько минут не выключая компьютер.', 3, '7f000001', 'N', 1410223004, '', '', 0, NULL, 0, 0, 'A', 1),
    (91, 215, 0, 'Илья Ефимов', NULL, NULL, 'Процессор отличный, 6 ядер и 12 потоков, в тестах кодирования впечатляющие результаты, в 3dmark vantage при 4.5ghz(воздух) набирает 41300 очков! Для игр можно e8400 брать и гнать до 4ггц.\n\nВообщем я о покупке не жалею.', 4, '7f000001', 'N', 1410266115, '', '', 0, NULL, 0, 0, 'A', 1),
    (92, 215, 0, 'Татьяна Гнедова', NULL, NULL, 'Купил себе эту \"игрушку\",очень доволен!\n\nВо первых он решает сразу все мои задачи по максимуму. А это работы в программах типа 3DS MAx и прочих,хорошо жрущих ресурсы.', 3, '7f000001', 'N', 1410300056, '', '', 0, NULL, 0, 0, 'A', 1),
    (93, 217, 0, 'Илья Ефимов', NULL, NULL, 'Купил месяца полтора назад. Работает очень быстро и без сбоев, тихо. Оценка 4 так как сначала было не привычно пользоваться вертикальным приводом CD и DVD дисков, но потом это не вызвало неудобств. Так же отмечу из плюсов два порта USB на передней панели. Из минусов, что они за дверцей на которую надо нажать, чтобы до них добраться, но это для красоты:) Еще не с первого раза включается кнопка питания (просто надо посильней нажать), что для женщин может быть неудобно. Так же радует компактность блока.', 4, '7f000001', 'N', 1410324426, '', '', 0, NULL, 0, 0, 'A', 1),
    (94, 217, 0, 'Нина Гаврилова', NULL, NULL, 'Очень мощный компьютер, высокая производительность и быстродействие. Отлично воспроизводит видео и игры без торможения.', 5, '7f000001', 'N', 1410379746, '', '', 0, NULL, 0, 0, 'A', 1),
    (95, 218, 0, 'Елена Иванова', NULL, NULL, 'Дизайн в порядке не настолько впечатляющий. Графика интегрирована средствами ATI Radeon HD300, этот процессор предназначен для игр. Скорость работы отличная. Это процессор хорош.\n\nДовольно дорогой, но стоит того, совместим с любым монитором.', 3, '7f000001', 'N', 1410427646, '', '', 0, NULL, 0, 0, 'A', 1),
    (96, 218, 0, 'Татьяна Гнедова', NULL, NULL, 'Системный блок является достаточно мощным для игр. Он также позволяет играть с DVD-дисков. Четко воспроизводит потоковое видео с веб-сайтов, таких как YouTube.', 4, '7f000001', 'N', 1410475403, '', '', 0, NULL, 0, 0, 'A', 1),
    (97, 219, 0, 'Антон Михайлов', NULL, NULL, 'В пользовании меньше месяца. Хорошая машина для дома, кому требуется достаточная производительность и адекватная цена. Немного толстовата для современных мобильных ПК, зато есть хороший Salent режим, в котором ноутбук совершенно не слышно, вдобавок засыпает и просыпается очень быстро, ни каких лампочек в режиме сна не мигает (кроме зарядки, если она подключена).\n\nПорадовало ПО, всё по делу. Даже есть разбивка жёсткого при первом включении.\n\nЦветовые решения очень порадуют девушек.\n\nЭкран вполне подойдёт для рабочего использования и интернет серфинга, фото на нём обрабатывать нельзя.\n\nСтавлю 5, а минусы совершенно не меняют картины, если вы знаете что вы берёте и для чего.', 5, '7f000001', 'N', 1410496630, '', '', 0, NULL, 0, 0, 'A', 1),
    (98, 219, 0, 'Юрий Кондрашов', NULL, NULL, 'Пользовался больше года, вначале купил просто что бы можно было ходить на кухне, ноутбук справился со всем на \"Ура\".\n\nАвтономное время работы совпало с заявленным, что тоже обрадовало.\n\nВ общем, за свои деньги отработал на все 100%, да еще и с лихвой.', 5, '7f000001', 'N', 1410542310, '', '', 0, NULL, 0, 0, 'A', 1),
    (99, 220, 7, 'Николай Ворошилов', NULL, NULL, 'Дизайн по мне отличный. Выглядит тонким, и реально не толстый, не тяжелый, время автономии приличное, ходил в библиотеку на день, батареи хватало, шнур не нужен.\n\nЕсть подсветка, но она меня огорчает. Приходится включать фонарик или лампу. Сама клавиатура у меня неровная, она как небольшая волна, сразу не заметил, потом присмотрелся к отпечаткам на экране и уже поздно думаю менять. Опять же присмотрелся и увидел, как в углах неровно скреплены детали, вроде мелочь. За год стал скрипеть немного, в левой части вверху и внизу ощутимо. Видел у коллеги такой же и, сравнив, мы поняли, что системная особенность.', 4, '7f000001', 'N', 1410593986, '', '', 0, NULL, 0, 0, 'A', 1),
    (100, 220, 0, 'Татьяна Вершкова', NULL, NULL, 'Не думала, что у аппарата за такие деньги могут быть такие недостатки.\n\nКачество экрана не удовлетворяет. Небольшие углы обзора, разрешение.\n\nЕсть куча настроек, для меня абсолютно бесполезных. Греется довольно сильно. Качество корпуса оставляет желать лучшего. Местами скрипит, проминается под нажатием пальцев. Почему не сделали металл по всей поверхности, а не только там где кнопки, не понятно. Так бы и выглядел лучше и возможно не скрипел бы.\n\nПро звук ничего говорить не буду. Лучше через штатные динамики его не слушать.\n\nОт батареи работает часа 1.5, если смотреть видео. Посмотреть фильм полностью на одном заряде батареи не возможно.\n\nНо с этим всем я бы мог смириться, если бы не кнопки. Они стали отваливаться уже через несколько месяцев эксплуатации. Возвращать их на место можно несколько раз, но, в конце концов, их хлипкие крепления ломаются, и кнопки перестают держаться на месте. Лучше бы были обычные кнопки, без подсветки, но которые надежно держаться и не отваливаются.\n\nИ это отнюдь далеко не самая бюджетная модель в линейке.\n\nНе рекомендую.', 2, '7f000001', 'N', 1410639697, '', '', 0, NULL, 0, 0, 'A', 1),
    (101, 221, 0, 'Елена Круглова', NULL, NULL, 'Менее года назад приобрела данную модель ноутбука. При таком уровне далеко даже не средней цены, удивили те \"мелочи\", которые не предусмотрены при разработке:\n\n- изоляция блока питания порвалась (несмотря на аккуратную эксплуатацию).\n\n- очень маркий корпус (отпечатки пальцев везде. Можно использовать только в перчатках)\n\n- материал, которые обрамляет клавиатуру весь потрескался)\n\nВнутренности все более чем устраивают, хотя есть ряд ошибок, которые выдаются на систематической основе:\n\n- сетевой адаптер плохо ловит сеть (хотя стоит драйвер последней версии).\n\n- при закрытии крышки и переходе в спящий режим, через некоторое время сам перезагружается, не сохраняя последние версии доков.', 2, '7f000001', 'N', 1410697269, '', '', 0, NULL, 0, 0, 'A', 1),
    (102, 221, 0, 'Олег Диментьев', NULL, NULL, 'Единственный недостаток в работе - подсветка клавиатуры подсвечивает латинский алфавит,а кириллица за счёт контражура видна ещё хуже.\n\nПоложительные моменты: качественный звук, громкость, привлекательный дизайн, цвет, небольшой вес, габариты, естественная цветопередача.', 4, '7f000001', 'N', 1410703945, '', '', 0, NULL, 0, 0, 'A', 1),
    (103, 222, 0, 'Михаил Новиков', NULL, NULL, 'Купил из за мощной видео карты!\n\nПоложительные моменты: надежность, качественный звук, громкость, привлекательный дизайн, цвет, небольшой вес, габариты, естественная цветопередача, оптимальное соотношение цена/качество.', 5, '7f000001', 'N', 1410774585, '', '', 0, NULL, 0, 0, 'A', 1),
    (104, 222, 0, 'Александр Дмитриев', NULL, NULL, 'Отличнейший мультимедийный ноутбук. Все, что нужно есть. Правда в супер игровые новинки не поиграешь из-за встроенной видеокарты. Заряд держит очень долго, стильный.\n\nОтрицательные моменты: Слишком большой размер и вес', 4, '7f000001', 'N', 1410796988, '', '', 0, NULL, 0, 0, 'A', 1),
    (105, 223, 0, 'Иван Панов', NULL, NULL, 'Пользуюсь уже год и планшет в отличном состоянии до сих пор. Глючил bluetooth, иногда не хотел работать WIFI, не удалялись некоторые приложения и частенько зависали. Однако, должен заметить, служил он верой и правдой и все функции (чтение книг и учебников), просмотр фильмов, интернет и работа с файлами - выполнялись достойно.\n\nПосле прошивки на официальную 4.0.4 не могу нарадоваться - все глюки будто рукой сняло - нормальная поддержка многоядерности - все стало летать как ракета.', 5, '7f000001', 'N', 1410860191, '', '', 0, NULL, 0, 0, 'A', 1),
    (106, 223, 0, 'Ольга Дмитриева', NULL, NULL, 'Поскольку сравнивать особо не с чем, это мой первый планшет, тормозов не замечено. Перед покупкой внимательно рассматривал Sony и Apple. Результатом сравнения массы параметров стал Samsung.\n\nИнтернет тормозит, но дома есть интернет через wifi, на который планшет автоматически переходит и все летает. Никаких неудобств с расположением динамиков и интерфейсов также нет, достаточно перевернуть экран. Время непрерывной работы около 10-12 часов. Если отключить сигналы, уведомления, gps и прочее в данный момент ненужное, то батарейки хватит надолго.\n\nПокупкой доволен, рекомендую остальным.', 4, '7f000001', 'N', 1410879504, '', '', 0, NULL, 0, 0, 'A', 1),
    (107, 225, 0, 'Антон Михайлов', NULL, NULL, 'Яркий дисплей, недорогой, легкий, простой для хранения и чтения, расширяемой памяти через микро-SD. Есть настройки для доступа и управления домашним компьютером, через Интернет.\n\nИдеальный размер - я могу держать его одной рукой.', 5, '7f000001', 'N', 1410957294, '', '', 0, NULL, 0, 0, 'A', 1),
    (108, 225, 0, 'Елена Бушуева', NULL, NULL, 'Приобрела Galaxy Tab 7 почти месяц назад, и я действительно впечатлена. E-mail, веб-браузер, карты, календарь, текстовые сообщения, так много функций. Так легко носить с собой по сравнению с IPAD.\n\nОт всей души рекомендую его другим.', 4, '7f000001', 'N', 1410972925, '', '', 0, NULL, 0, 0, 'A', 1),
    (109, 226, 0, 'Елена Николаева', NULL, NULL, 'Положительные моменты платформы в целом: трехканальная память дает 6 слотов, 6 гб. на 3 планках идеально подходит для 64-битной ОС.\n\nЦена более чем адекватна!', 5, '7f000001', 'N', 1411027249, '', '', 0, NULL, 0, 0, 'A', 1),
    (110, 226, 0, 'Олег Фуражкин', NULL, NULL, 'Решив перейти на i7, я купил этот процессор, так как на данный момент только он единственный из своей линейки обладает приемлемой ценой. Температура без нагрузки 51-53 в пассивном режиме, 47-48 в активном. При полной нагрузке температура приближается к 80 градусам.', 4, '7f000001', 'N', 1411062234, '', '', 0, NULL, 0, 0, 'A', 1),
    (111, 227, 0, 'Иван Колышев', NULL, NULL, 'Я купил этот монитор еще в середине апреля. Прочитал много отзывов, большинство было отрицательных. Этот монитор является фантастическим. У него нет никаких проблем с цветом. Я купил дополнительно пару 3D очков Nvidia, так что я могу поделиться абсолютно потрясающими впечатлениями от этого монитора. Мало того, что экран как-будто выскакивает на тебя, но он также имеет большую глубину. У меня есть большая коллекция Blue Ray фильмов. С этим монитором я вижу детали, которые не видел прежде. Цвета яркие и Тона точны.\n\nВсем советую!', 4, '7f000001', 'N', 1411108362, '', '', 0, NULL, 0, 0, 'A', 1),
    (112, 227, 0, 'Леонид Ефимов', '+ Отличный во всех отношениях монитор', '- Медленно пробуждается из спящего режима', 'У меня было 4 различных 3D монитора на протяжении многих лет, но этот монитор оказался лучшим. Что касается возможности 3D, изображение четкое, без искажений. Монитор также поддерживает высокий уровень яркости. Экран 27-дюймовый просто превосходный. Цвета, яркость и контрастность отличные. Этот монитор можно использовать, даже если вам не нужна функция 3D. Единственный недостаток, который я нашел монитор медленно пробуждается из спящего режима. В целом, я оцениваю монитор в 5 звезд.', 5, '7f000001', 'N', 1411171560, '', '', 0, NULL, 0, 0, 'A', 1),
    (113, 229, 0, 'Светлана Виноградова', NULL, NULL, 'Монитор HP превысило мои самые смелые ожидания. Монитор очень яркий, цветопередача отличная. С точки зрения остроты это фантастика. Этот монитор очень легкий. Нет мертвых пикселей. Я рад, что приобрел его, и я рекомендую его друзьям. Я не могу себе представить человека, недовольного данным монитором.', 4, '7f000001', 'N', 1411175490, '', '', 0, NULL, 0, 0, 'A', 1),
    (114, 229, 0, 'Светлана Виноградова', NULL, NULL, 'Монитор действительно является большим. Я представлял себе хороший монитор, но этот монитор превзошел мои ожидания. LED достаточно яркий.\n\nИгры, фильмы выглядят очень хорошо и богато.', 3, '7f000001', 'N', 1411223785, '', '', 0, NULL, 0, 0, 'A', 1),
    (115, 240, 0, 'Нина Цифаркина', NULL, NULL, 'В своей ценовой категории это самый надежный и прочный продукт. Прекрасно ориентируется в пространстве. Сверхчувствительный сенсор, отличная картинка. Время работы наивысшее во всей линейке товаров.\n\nК сожалению нет Flash, но это компенсируется специальной программой (правда она не лицензионная). Отсутствие разъёма USB компенсируется переходником.', 3, '7f000001', 'N', 1411302040, '', '', 0, NULL, 0, 0, 'A', 1),
    (116, 240, 0, 'Виктория Краснова', NULL, NULL, 'Графика, удобство использования, огромное количество приложений, защищенность. Относительный недостаток батарея, зависит от яркости подсветки и запущенных приложений, порой вылетает из некоторых приложений.\n\nПрекрасная вещь! Очень рада, что отговорили брать galaxy tab. Не расстаюсь с ним, вся жизнь теперь в нем. Безумно удобен в учебе, удобно читать книги. Бесплатных приложений вполне хватает для работы и учебы.', 4, '7f000001', 'N', 1411332142, '', '', 0, NULL, 0, 3, 'A', 1),
    (117, 241, 0, 'Нина Круглов', NULL, NULL, 'Отличный планшет!!! Вот подарили на день рождения, пользуюсь уже 6 месяцев и ни как не нарадуюсь!!!Игры, Фильмы, Музыку качает отменно!Отличная графика, контрастные фотографии, всё видно прекрасно! Давно хотела себе именно ЭТОТ планшет, и мечтания сбылись!!!\n\nСоветую ВСЕМ!', 5, '7f000001', 'N', 1411389967, '', '', 0, NULL, 0, 4, 'A', 1),
    (118, 241, 0, 'Валентина Андреева', NULL, NULL, 'Отличная вещь. Купила месяц назад и все свободное время провожу с ним. Если кто хочет приобрести,не сомневайтесь, никакие другие производители не сравнятся с iPad.', 4, '7f000001', 'N', 1411400609, '', '', 0, NULL, 0, 4, 'A', 1),
    (119, 244, 0, 'Алексей Моисеев', '+ Внешний вид\r\n', '- Слабое железо.\r\n- Матрица по углам и цветам плохая.\r\n- Один пиксель битый.\r\n- Из колонок независимо от ОС и драйверов постоянно слышен назойливый противный фон.', 'Взял две штуки на работу в надежде поставить на них Убунту. Это оказалось неожиданно сложно в силу каких-то проблем с железом и BIOS. С горем пополам поставили - но работает убунту на нем очень плохо из-за проблем с поддержкой видео.\r\n\r\nКонкретно эту модель брать не советую, совсем она неудачная.', 2, '7f000001', 'N', 1411461376, '', '', 1, 'Сожалеем, что вы столкнулись с проблемой. Напишите на sales@example.com, посмотрим, что можно сделать. Заводской брак бывает у любого товара. Единственное, у производителя на сайте есть свои правила на количество битых пикселей, при котором экран считают бракованным.', 1618495159, 3, 'A', 1),
    (120, 244, 0, 'Олег Крупицин', '+ Цена низкая\r\n+ Для офисной работы\r\n+ Тихий', '- Проблема с драйверами. На официальном сайте такой модели вообще не значится и драйверов под нее соответственно нет.', 'Для любителей повозиться с техникой на досуге.', 1, '7f000001', 'N', 1411501920, '', '', 0, NULL, 0, 4, 'A', 1),
    (121, 249, 0, 'Михайлова Елена', '+ Контроллеры можно подключить к портативному экрану\r\n+ Удобный софт', '', 'Приставка очень классная! Контроллер с экраном это очень удобная вещь! Удобно играть (игры специально сделаны для планшета, куча фишек), удобно пользоваться интернетом (пишу с него же), браузер очень продуманный, удобно общаться в сети и делать покупки в специальном магазине!\r\n\r\nО магазине - он тоже очень удобный и простой! Быстро покупать, есть акции где можно купить интересные игрушки за не большую цену! Купила её из-за \"Бетмена\", очень классно сделана, играть одно удовольствие! Да и цены, как многие пишут, не выше чем у конкурентов!', 5, '7f000001', 'N', 1412340449, '', '', 0, NULL, 0, 4, 'A', 1),
    (122, 249, 0, 'Волина Ольга', NULL, NULL, 'Новая консоль от Nintendo, теперь с графикой уровня Xbox 360 и PS3. Для игр в компании отлично подойдут игры вроде Nintendo Land, New Super Mario Bros. U и Rabbids Land. Связка ТВ плюс экран геймпада - это действительно весело и интересно.', 4, '7f000001', 'N', 1412340540, '', '', 0, NULL, 0, 3, 'A', 1),
    (123, 250, 0, 'Петров Сергей', '+ Дешевле, чем игровой ПК', '- Игры дороже, чем на компьютерах\r\n- Пока новых игр ещё мало', 'В общем, покупкой доволен. Всё оказалось так, как я и ожидал. Если у вас есть деньги и страстное желание прикоснуться к некстгену, нет довольно мощного ПК, где можно поиграть в большинство игр из магазина Sony по гораздо более низким ценам, и просто стать одним из первых обладателей приставки - можете смело покупать. Всем остальным можно не спешить и дождаться большего количества эксклюзивов, выход которых намечен ближе к осени.', 4, '7f000001', 'N', 1412340786, '', '', 0, NULL, 0, 4, 'A', 1),
    (124, 250, 0, 'Устимова Анна', NULL, NULL, 'Отличная консоль. Пока только радует. Вообще поколение только разгоняется, и какие-либо существенные недостатки указать сложно.', 4, '7f000001', 'N', 1412340878, '', '', 0, NULL, 0, 4, 'A', 1),
    (125, 282, 0, 'Юрий', 'Сидит идеально', '', 'Впервые заказал одежду онлайн. Даже зная размер, был риск, что футболка не подойдёт. Менеджер пообещал, что если не подойдёт, смогу без проблем вернуть. Но это не потребовалось. Зацените.', 5, '7f000001', 'N', 1618553915, '', '', 0, NULL, 0, 3, 'A', 1),
    (126, 280, 0, '', '', '', 'Мне не понравилась!', 2, '7f000001', 'N', 1618554043, '', '', 1, 'Расскажите пожалуйста, чем именно не понравился товар? Возможно, вы нашли какой-то дефект? Или не подошёл размер? В любом случае, напишите нам на почту или позвоните. Если возможно, постараемся решить проблему.', 1618554133, 4, 'A', 1),
    (127, 280, 0, 'Олег', '', '', 'Не понимаю, что тут может не понравиться. У меня уже несколько таких, разных цветов. Нареканий не было ни разу.', 5, '7f000001', 'N', 1618554551, '', '', 1, 'Спасибо вам за положительный отзыв и за то, что выбираете наш магазин!', 1618554680, 4, 'A', 1);
